import React, { useState, useEffect } from "react";
import menuData from "./menuData.json";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content: "Welcome to Chick-fil-A! We're so excited to see you! What can we get started for you today? ðŸ®",
    },
  ]);
  const [menu] = useState(menuData.menu);
  const persona = "Use a friendly and approachable tone, add a touch of wit and humor, and incorporate colloquialisms and everyday language to give your writing a down-to-earth quality. Write in a straightforward and concise manner, avoiding excessive use of flowery language or complex sentence structures. Keep your writing style relatable and easy to follow, to establish yourself as a friendly and likable person."
  const [order, setOrder] = useState([]);

  useEffect(() => {
    const storedMessages = localStorage.getItem("chat_history");
    if (storedMessages) {
      setMessages(JSON.parse(storedMessages));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chat_history", JSON.stringify(messages));
  }, [messages]);

  const getOrderedItems = async (chatHistory) => {
    const response = await fetch("/api/chatgpt", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders. The menu items available are: ${menu.map((item) => item.name).join(", ")}. ${chatHistory} Analyze the conversation and extract the order items, including their names, sizes, and quantities.`,
        temperature: 0.7,
        topP: 1,
        maxLength: 150,
      }),
    });

    const responseBody = await response.json();
    const chatGPTResponse = responseBody.content;
    const orderedItems = JSON.parse(chatGPTResponse);

    return orderedItems;
  };



  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages([...messages, { role: "user", content: inputText }]);

      const chatHistory = [...messages, { role: "user", content: inputText }].map((m) => m.content).join(" ");
      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders. The menu items available are: ${menu.map((item) => item.name).join(", ")}. ${chatHistory}`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      console.log("Raw response:", response);

      const responseBody = await response.json();
      console.log("Response body:", responseBody);

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }


      setInputText("");
const orderedItems = await getOrderedItems([...messages, { role: "user", content: inputText }].map((m) => m.content).join(" "));
console.log("Chat history:", [...messages, { role: "user", content: inputText }]);
console.log("Ordered items:", orderedItems);


    }
  };

  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <div className="order-container">
  <h2>Your Order</h2>
  {order.length > 0 ? (
    <ul>
      {order.map((item, index) => (
        <li key={index}>
          {item.name} - {item.size ? `${item.size} ` : ""}{item.quantity > 1 ? `(${item.quantity})` : ""}
        </li>
      ))}
    </ul>
  ) : (
    <p>No items in your order.</p>
  )}
</div>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
