import React, { useState, useEffect } from "react";
import menuData from "./menuData.json";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content: "Welcome to Chick-fil-A! We're so excited to see you! What can we get started for you today? 🐮",
    },
  ]);
  const [menu] = useState(menuData.menu);
  const persona = "Use a friendly and approachable tone, add a touch of wit and humor, and incorporate colloquialisms and everyday language to give your writing a down-to-earth quality. Write in a straightforward and concise manner, avoiding excessive use of flowery language or complex sentence structures. Keep your writing style relatable and easy to follow, to establish yourself as a friendly and likable person."
  const [order, setOrder] = useState([]);

  useEffect(() => {
    const storedMessages = localStorage.getItem("chat_history");
    if (storedMessages) {
      setMessages(JSON.parse(storedMessages));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chat_history", JSON.stringify(messages));
  }, [messages]);

  // Helper function to extract menu items from the conversation
const extractOrderItems = (messages) => {
  const orderedItems = [];
  const regex = new RegExp(`(${menu.map((item) => item.name).join("|")})`, "i");

  messages.forEach((message, index) => {
    if (message.role === "user") {
      const matches = message.content.match(regex);
      if (matches && matches.length > 0) {
        let size = "";
        let quantity = 1;

        if (
          messages[index + 1] &&
          messages[index + 1].role === "assistant" &&
          /what size/i.test(messages[index + 1].content)
        ) {
          const sizeMatches = messages[index + 2].content.match(/(small|medium|large)/i);
          if (sizeMatches && sizeMatches.length > 0) {
            size = sizeMatches[0];
          }
        }

        orderedItems.push({
          name: matches[0],
          size: size,
          quantity: quantity,
        });
      }
    }
  });

  return orderedItems;
};

// Updates the customer's order
const updateOrder = (items) => {
  items.forEach((item) => {
    const menuItem = menu.find((mItem) => mItem.name.toLowerCase() === item.name.toLowerCase());
    if (menuItem) {
      const newItem = {
        id: menuItem.id,
        name: item.name,
        size: item.size,
        quantity: item.quantity,
      };
      setOrder((prevOrder) => [...prevOrder, newItem]);
    }
  });
};


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages([...messages, { role: "user", content: inputText }]);

      const chatHistory = [...messages, { role: "user", content: inputText }].map((m) => m.content).join(" ");
      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders. The menu items available are: ${menu.map((item) => item.name).join(", ")}. ${chatHistory}`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      console.log("Raw response:", response);

      const responseBody = await response.json();
      console.log("Response body:", responseBody);

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }
      // Inside handleSubmit function, before setInputText("");
      const orderedItems = extractOrderItems([...messages, { role: "user", content: inputText }]);
      updateOrder(orderedItems);

      setInputText("");
    }
  };

  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
