import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages([...messages, { role: 'user', content: inputText }]);

      const response = await fetch('/api/chatgpt', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          prompt: inputText,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      const chatGPTResponse = await response.json();
      setMessages((prevMessages) => [
        ...prevMessages,
        { role: 'assistant', content: chatGPTResponse },
      ]);

      setInputText('');
    }
  };

  return (
    <div className="App">
      <h1>Chick-fil-A Chat Assistant</h1>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${message.role === 'assistant' ? 'assistant-message' : 'user-message'}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
