import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await axios.post('http://localhost:3001/api/chatgpt', {
        prompt: input,
        temperature: 0.8,
        topP: 0.9,
        maxLength: 150,
      });
      const lastMessage = result.data.choices[0].messages.slice(-1)[0];
      if (lastMessage.role === 'assistant') {
        setResponse(lastMessage.content);
      } else {
        console.error('Unexpected message role:', lastMessage.role);
      }
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
      <p>{response}</p>
    </div>
  );
}

export default App;
