import React, { useState, useEffect, useCallback } from "react";
import menuData from "./menuData.json";
import "./App.css";
import ChatContainer from "./components/ChatContainer";
import OrderContainer from "./components/OrderContainer";
import useLocalStorage from "./hooks/useLocalStorage";
import { overseer, extractAndFind, setCustomerOrder } from "./utils/chatUtils";
import { persona } from "./persona";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useLocalStorage("chat_history", [
    {
      role: "assistant",
      content: "Welcome to Chick-fil-A! We're so excited to see you! What can we get started for you today? ðŸ®",
    },
  ]);
  const [menu] = useState(menuData.menu);
  const [order, setOrder] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { role: "user", content: inputText },
      ]);

      const chatHistory = messages.map((m) => m.content).join(" ");

      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI server for Chick-fil-A, helping customers place orders using this ${persona}. The menu items available are: ${menu.map((item) => item.name).join(", ")}. ${chatHistory}`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      const responseBody = await response.json();

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }

      setInputText("");
    }
  };

  const overseerCallback = useCallback(async () => {
    const overseerResponse = await overseer(messages, menu);
    const itemIds = extractAndFind(overseerResponse, menu);
    const newOrder = setCustomerOrder(itemIds, menu);
    setOrder(newOrder);
    console.log("Overseer:", overseerResponse);
  }, [messages, menu]);

  useEffect(() => {
    if (messages.length > 1) {
      overseerCallback();
    }
  }, [messages, menu, overseerCallback]);

  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <OrderContainer order={order} />
      <ChatContainer
        messages={messages}
        inputText={inputText}
        setInputText={setInputText}
        handleSubmit={handleSubmit}
      />
    </div>
  );
}

export default App;
