import React, { useState, useEffect, useRef } from "react";
import menuData from "./menuData.json";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: "Welcome to Chick-fil-A! We're so excited to see you! What can we get started for you today? ðŸ®",
    },
  ]);

  const [menu] = useState(menuData.menu);
  const [order, setOrder] = useState([]);
  const persona = "Use a friendly and approachable tone, add a touch of wit and humor, and incorporate colloquialisms and everyday language to give your writing a down-to-earth quality. Write in a straightforward and concise manner (preferably under 60 characters), avoiding excessive use of flowery language or complex sentence structures. Keep your writing style relatable and easy to follow, to establish yourself as a friendly and likable person."
  const chatContainerRef = useRef(null);


  // Load messages from local storage
  useEffect(() => {
    const storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
    setMessages(storedMessages);
  }, []);

  // Save individual messages to local storage
  useEffect(() => {
    const lastMessage = messages[messages.length - 1];
    if (lastMessage) {
      let storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
      storedMessages.push(lastMessage);
      localStorage.setItem("messages", JSON.stringify(storedMessages));
    }
  }, [messages]);


  // review conversation and update order per review
  const reviewOrderItems = async () => {
    const chatHistory = messages
      .map((m) => m.content)
      .join(" ");

      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `Analyze the following conversation at a Chick-fil-A restaurant: ${chatHistory}. Based on the conversation, provide the menu items in the customer's order in the format "Menu item, Quantity, Modifications" (if any). Available menu items: ${menu
            .map((item) => item.name)
            .join(", ")}. Do not generate any additional responses as a customer or AI assistant.`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });


    const responseBody = await response.json();

    if (responseBody && responseBody.content) {
      const chatGPTResponse = responseBody.content;
      console.log("Menu Response:", chatGPTResponse);
      console.log("Chatlog", chatHistory)

      // Parse the response using regex
      const menuItemRegex = new RegExp(
        `\\b(?:${menuData.menu.map((item) => item.name).join("|")})\\b,\\s*(\\d+)(?:,\\s*([^,]*))?`,
        "ig"
      );

      const menuItemMatches = [...chatGPTResponse.matchAll(menuItemRegex)];

if (menuItemMatches) {
  const newOrder = [];

  for (const match of menuItemMatches) {
    const itemName = match[0];
    const quantity = parseInt(match[1], 10);
    const modifications = match[2];

    const menuItem = menu.find(
      (item) => item.name.toLowerCase() === itemName.toLowerCase()
    );

    if (menuItem) {
      const existingOrderItem = newOrder.find(
        (item) => item.id === menuItem.id
      );

      if (existingOrderItem) {
        existingOrderItem.quantity += quantity;
        existingOrderItem.modifications = modifications || "";
      } else {
        newOrder.push({
          ...menuItem,
          quantity,
          modifications: modifications || "",
        });
      }
    }
  }

  // Update the setOrder array if there are changes
  if (JSON.stringify(order) !== JSON.stringify(newOrder)) {
    setOrder(newOrder);
  }
}

    }
  };


// order
useEffect(() => {
  setOrder([]);
}, []);

// Render and update menuOrder dynamically
const renderOrder = () => {
  return order.map((item) => (
    <div key={item.id}>
      {item.name} x {item.quantity}
    </div>
  ));
};


const handleSubmit = async (e) => {
  e.preventDefault();

  if (inputText.trim()) {
    setMessages([...messages, { role: "user", content: inputText }]);

    const chatHistory = [...messages, { role: "user", content: inputText }]
      .map((m) => m.content)
      .join(" ");

    const response = await fetch("/api/chatgpt", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders under this ${persona}. The menu items available are: ${menu
          .map((item) => item.name)
          .join(", ")}. Your task is to understand and process customer's requests for adding, updating, or removing items from their order, and provide information about menu items. ${chatHistory}. Please keep your response concise.`,
        temperature: 0.7,
        topP: 1,
        maxLength: 150,
      }),
    });

    const responseBody = await response.json();

    if (responseBody && responseBody.content) {
      const chatGPTResponse = responseBody.content;
      console.log("ChatGPT Response:", chatGPTResponse);

      // Call the reviewOrderItems function to update the order
      await reviewOrderItems(chatGPTResponse);

      setMessages((prevMessages) => [
        ...prevMessages,
        { role: "assistant", content: chatGPTResponse },
      ]);
    } else {
      console.log("No response content:", responseBody);
    }

    setInputText("");
  }
};


  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);


  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <div className="chat-container" ref={chatContainerRef}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>

      <div className="menu-order">
        <h2>Your Order:</h2>
        {renderOrder()}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;

