import React, { useState, useEffect, useRef } from "react";
import menuData from "./menuData.json";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);
  const [menu] = useState(menuData.menu);
  const [order, setOrder] = useState([]);
  const persona = "Use a friendly and approachable tone, add a touch of wit and humor, and incorporate colloquialisms and everyday language to give your writing a down-to-earth quality. Write in a straightforward and concise manner (preferably under 60 characters), avoiding excessive use of flowery language or complex sentence structures. Keep your writing style relatable and easy to follow, to establish yourself as a friendly and likable person."
  const chatContainerRef = useRef(null);

  // Load messages from local storage
  useEffect(() => {
    const storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
    setMessages(storedMessages);
  }, []);

  // Save individual messages to local storage
  useEffect(() => {
    const lastMessage = messages[messages.length - 1];
    if (lastMessage) {
      let storedMessages = JSON.parse(localStorage.getItem("messages")) || [];
      storedMessages.push(lastMessage);
      localStorage.setItem("messages", JSON.stringify(storedMessages));
    }
  }, [messages]);

// order
useEffect(() => {
  setOrder([]);
}, []);

// Render and update menuOrder dynamically
const renderOrder = () => {
  return order.map((item) => (
    <div key={item.id}>
      {item.name} x {item.quantity}
    </div>
  ));
};


const extractInfo = (responseText) => {
  const menuItemNames = menuData.menu.map((item) => item.name).join('|');
  const addActionRegex = new RegExp(`(?:add|get|give me|add to my order|I want|I would like|include)\\s*(?:a |an |some |)(${menuItemNames})(?:please|)`, 'i');
  const updateActionRegex = /(?:change|update|alter|switch|replace|adjust)(?: the| my)?(?: quantity of)?(?: the)? ([a-zA-Z\s]+) (?:to|for|in my order)?(?:\s*please\s*|)(?:\s*to\s*|)(\d+)/i;
  const deleteActionRegex = /(?:remove|delete|cancel|take off|erase|discard)(?: the| a| an)? ([a-zA-Z\s]+)(?: from my order| from the order|)?(?: please|)/i;

  const addActionMatch = responseText.match(addActionRegex);
  const updateActionMatch = responseText.match(updateActionRegex);
  const deleteActionMatch = responseText.match(deleteActionRegex);

  if (addActionMatch) {
    return {
      action: "add",
      item: {
        name: addActionMatch[1].trim(),
        quantity: null,
      },
    };
  } else if (updateActionMatch) {
    return {
      action: "update",
      item: {
        name: updateActionMatch[1].trim(),
        quantity: parseInt(updateActionMatch[2]),
      },
    };
  } else if (deleteActionMatch) {
    return {
      action: "delete",
      item: {
        name: deleteActionMatch[1].trim(),
        quantity: null,
      },
    };
  } else {
    return {
      action: "",
      item: {
        name: "",
        quantity: null,
      },
    };
  }
};



  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages([...messages, { role: "user", content: inputText }]);

      const chatHistory = [...messages, { role: "user", content: inputText }]
        .map((m) => m.content)
        .join(" ");
      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders under this ${persona}. The menu items available are: ${menu
            .map((item) => item.name)
            .join(", ")}. Your task is to understand and process customer's requests for adding, updating, or removing items from their order, and provide information about menu items. ${chatHistory}`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      console.log("Raw Response:", response);

      const responseBody = await response.json();
      console.log("Response body:", responseBody);

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        // Extract relevant information from the ChatGPT response
        const extractedInfo = extractInfo(chatGPTResponse);
        console.log("Extracted info:", extractedInfo); // Moved this line here

        // Perform CRUD operation based on the extracted information
        switch (extractedInfo.action) {
          case "add":
            const itemToAdd = menu.find(
              (item) =>
                item.name.toLowerCase() === extractedInfo.item.name.toLowerCase()
            );
            if (itemToAdd) {
              setOrder((prevOrder) => {
                const existingOrderItem = prevOrder.find(
                  (item) => item.id === itemToAdd.id
                );
                if (existingOrderItem) {
                  return prevOrder.map((item) =>
                    item.id === itemToAdd.id
                      ? { ...item, quantity: item.quantity + 1 }
                      : item
                  );
                } else {
                  return [...prevOrder, { ...itemToAdd, quantity: 1 }];
                }
              });
            }
            break;

          case "update":
            const itemToUpdate = order.find(
              (item) =>
                item.name.toLowerCase() === extractedInfo.item.name.toLowerCase()
            );
            if (itemToUpdate) {
              console.log("Order before update:", order);
              itemToUpdate.quantity = extractedInfo.item.quantity;
              setOrder([...order]);
              console.log("Order after update:", order);
            }
            break;

          case "delete":
            const itemToDeleteIndex = order.findIndex(
              (item) =>
                item.name.toLowerCase() === extractedInfo.item.name.toLowerCase()
            );
            if (itemToDeleteIndex > -1) {
              console.log("Order before delete:", order);
              setOrder(order.filter((_, index) => index !== itemToDeleteIndex));
              console.log("Order after delete:", order);
            }
            break;

          default:
            break;
        }

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }

      setInputText("");
    }
  };


  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);


  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <div className="chat-container" ref={chatContainerRef}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      {/* Add renderOrder here */}
      <div className="menu-order">
        <h2>Your Order:</h2>
        {renderOrder()}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
