const handleSubmit = async (e) => {
  e.preventDefault();

  if (inputText.trim()) {
    setMessages([...messages, { role: "user", content: inputText }]);

    const chatHistory = [...messages, { role: "user", content: inputText }]
      .map((m) => m.content)
      .join(" ");

    const response = await fetch("/api/chatgpt", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders under this ${persona}. The menu items available are: ${menu
          .map((item) => item.name)
          .join(", ")}. Your task is to understand and process customer's requests for adding, updating, or removing items from their order, and provide information about menu items. Given this conversation, what is the customer's current order? ${chatHistory}`,
        temperature: 0.7,
        topP: 1,
        maxLength: 150,
        menuData: menuData,
      }),
    });

    const responseBody = await response.json();

    if (responseBody && responseBody.content) {
      const chatGPTResponse = responseBody.content;
      setMessages((prevMessages) => [
        ...prevMessages,
        { role: "assistant", content: chatGPTResponse },
      ]);

      // Make a separate API call to update the order
      const orderResponse = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI assistant for Chick-fil-A, helping customers place orders under this ${persona}. The menu items available are: ${menu
            .map((item) => item.name)
            .join(", ")}. Your task is to understand and process customer's requests for adding, updating, or removing items from their order, and provide information about menu items. Given this conversation, what is the customer's updated order? ${chatHistory}`,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
          menuData: menuData,
        }),
      });

      const orderResponseBody = await orderResponse.json();

      if (orderResponseBody && orderResponseBody.content) {
        // Process the updated order from the API response
        const updatedOrder = processOrder(orderResponseBody.content);
        setOrder(updatedOrder);
      }
    }
  }
};

const processOrder = (orderString) => {
  const regex = new RegExp(`(\\d+)\\s*x\\s*(${menu.map((item) => item.name).join("|")})`, "gi");
  const orderItems = [];

  let match;
  while ((match = regex.exec(orderString)) !== null) {
    const quantity = parseInt(match[1]);
    const itemName = match[2];
    const menuItem = menu.find((item) => item.name === itemName);

    if (menuItem) {
      orderItems.push({ ...menuItem, quantity });
    }
  }

  return orderItems;
};


