import React, { useState, useEffect, useCallback } from "react";
import menuData from "./menuData.json";
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content: "Welcome to Chick-fil-A! We're so excited to see you! What can we get started for you today? üêÆ",
    },
  ]);
  const [menu] = useState(menuData.menu);
  const persona = "Use a friendly and approachable tone, add a touch of wit and humor, and incorporate colloquialisms and everyday language to give your writing a down-to-earth quality. Include an emoji in your message to convey personality. Write in a straightforward and concise manner (preferably under 50 characters), avoiding excessive use of flowery language or complex sentence structures. Keep your writing style relatable and easy to follow, to establish yourself as a friendly and likable person."
  const [order, setOrder] = useState([]);

  useEffect(() => {
    const storedMessages = localStorage.getItem("chat_history");
    if (storedMessages) {
      setMessages(JSON.parse(storedMessages));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chat_history", JSON.stringify(messages));
  }, [messages]);


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      const newMessages = [...messages, { role: "user", content: inputText }];
      setMessages(newMessages);

      const chatHistory = newMessages.map((m) => m.content).join(" ");

      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: `You are an AI server for Chick-fil-A, helping customers place orders using this ${persona}. The menu items available are: ${menu.map((item) => item.name).join(", ")}. ${chatHistory} Do not recommend menu items if the customer hasn't completed their first menu item.`,
          temperature: 0,
          topP: 1,
          maxLength: 150,
        }),
      });

      const responseBody = await response.json();

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }

      setInputText("");
    }
  };


  const overseer = useCallback(async () => {
    const chatHistory = messages.map((m) => m.content).join(" ");

    const response = await fetch("/api/chatgpt", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        prompt: `Analyze this conversation: ${chatHistory}. Given this conversation and these menu items available here: ${menu.map((item) => item.name).join(", ")}, in an imperitive and concise manner return ONLY the NAMES of the menu items the user wants added to their order in a comma-separated list. Ignore the assistant`,
        temperature: 0,
        topP: 1,
        maxLength: 100,
      }),
    });

    const responseBody = await response.json();
    return responseBody.content;
  }, [messages, menu]);

  // Maybe the overseer should only view the customer's messages? How do I get the overseerer to pay attention to what the customer wants, not the recommendations

  const processOverseerResponse = useCallback(async () => {
    const overseerResponse = await overseer();
    const itemStrings = overseerResponse.split(", ");

    const newOrder = itemStrings.map((itemString) => {
      const [itemName, ...modifications] = itemString.split(" with ");
      const menuItem = menu.find((item) => item.name.toLowerCase() === itemName.toLowerCase());

      if (!menuItem) return null;

      const appliedModifications = modifications
        .map((mod) => menuItem.modifications.find((m) => m.name.toLowerCase() === mod.toLowerCase()))
        .filter((m) => m);

      return {
        ...menuItem,
        quantity: 1,
        modifications: appliedModifications,
      };
    }).filter((item) => item);

    setOrder(newOrder);
    console.log("Overseer:", overseerResponse);

  }, [menu, overseer]);


  useEffect(() => {
    if (messages.length > 1) {
      processOverseerResponse();
    }
  }, [messages, menu, overseer, processOverseerResponse]);


  return (
    <div className="App">
      <h1>Chick-fil-AI</h1>
      <div className="order-container">
  <h2>Your Order</h2>
  {order.length > 0 ? (
  <ul>
    {order.map((item, index) => (
      <li key={index}>
        {item.name} - {item.size ? `${item.size} ` : ""}{item.quantity > 1 ? `(${item.quantity})` : ""}
        {item.modifications && item.modifications.length > 0 && (
          <span> (with {item.modifications.map((mod) => mod.name).join(", ")})</span>
        )}
      </li>
    ))}
  </ul>
) : (
  <p>No items in your order.</p>
)}

</div>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
