import React, { useState, useEffect } from "react";
import menuData from './menuData.json';
import "./App.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);
  const [menu, setMenu] = useState(menuData);

  useEffect(() => {
    const storedMessages = localStorage.getItem("chat_history");
    if (storedMessages) {
      setMessages(JSON.parse(storedMessages));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chat_history", JSON.stringify(messages));
  }, [messages]);


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (inputText.trim()) {
      setMessages([...messages, { role: "user", content: inputText }]);

      const response = await fetch("/api/chatgpt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: inputText,
          temperature: 0.7,
          topP: 1,
          maxLength: 150,
        }),
      });

      console.log("Raw response:", response);

      const responseBody = await response.json();
      console.log("Response body:", responseBody);

      if (responseBody && responseBody.content) {
        const chatGPTResponse = responseBody.content;

        // Extract relevant information from the ChatGPT response
        const extractedInfo = extractInfo(chatGPTResponse);

        // Perform CRUD operation based on the extracted information
        switch (extractedInfo.action) {
          case "add":
            const itemToAdd = menu.find((item) => item.id === extractedInfo.item.id);
            if (itemToAdd) {
              itemToAdd.quantity = (itemToAdd.quantity || 0) + 1;
              setMenu([...menu]);
            }
            break;
          case "update":
            const itemToUpdate = menu.find((item) => item.id === extractedInfo.item.id);
            if (itemToUpdate) {
              itemToUpdate.quantity = extractedInfo.item.quantity;
              setMenu([...menu]);
            }
            break;
          case "delete":
            const itemToDeleteIndex = menu.findIndex((item) => item.id === extractedInfo.itemId);
            if (itemToDeleteIndex > -1) {
              setMenu(menu.filter((_, index) => index !== itemToDeleteIndex));
            }
            break;
          default:
            break;
        }

        setMessages((prevMessages) => [
          ...prevMessages,
          { role: "assistant", content: chatGPTResponse },
        ]);
      } else {
        console.log("No response content:", responseBody);
      }

      setInputText("");
    }
  };


  return (
    <div className="App">
      <h1>Chick-fil-A ChatGPT</h1>
      <div className="chat-container">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`chat-message ${message.role === "user" ? "user" : "assistant"}`}
          >
            {message.content}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default App;
